Tactic.SrcText:
//------------------------------------------------------------------------
// 简称: psy_trend_v1
// 名称: 
// 类别: 公式应用
// 类型: 用户应用
// 输出: Void
//------------------------------------------------------------------------
Params
	// 心理线指标统计过去12根Bar柱
	Numeric Length(12);
	// 心理线指标上轨
	Numeric PsyUpBand(75);
	// 心理线指标下轨
	Numeric PsyDnBand(25);
	// 初级止损
	Numeric InitialStop(20);
	// 保本止损
	Numeric BreakEvenStop(30);
	// 追踪止盈
	Numeric TrailingStop(50);

Vars
	// 入场后的最高价
	NumericSeries HigherAfterEntry(0);
	// 入场后的最低价
	NumericSeries LowerAfterEntry(0);
	// 心理线统计范围内阳线数
	NumericSeries m_PosKNum(0);
	// 心理线指标
	NumericSeries m_Psy(0);

	//多头行情
	Bool bLong(False);
	// 空头行情
	Bool bShort(False);
	// 止损/止盈线
	NumericSeries StopLine(0);
	// 保证金设置与仓位控制
	NumericSeries TotalEquity(0);
	NumericSeries UseMargin(0);
	NumericSeries EntryLots(0);
	// 初始资金1000000
	Numeric InitialEquity(1000000);
	// 仓位控制
	Numeric Prop(0.05);

Begin
	TotalEquity = 1000000+ Abs(CurrentContracts()*Close*ContractUnit()*BigPointValue()*MarginRatio());
     UseMargin = Close*ContractUnit*BigPointValue*MarginRatio;
     EntryLots = IntPart(TotalEquity*Prop/UseMargin);
      
     // 记录入场后的最高价和最低价 
	If(BarsSinceEntry >= 1){
		HigherAfterEntry = Max(HigherAfterEntry[1], High[1]);
		LowerAfterEntry = Min(LowerAfterEntry[1], Low[1]);
	}
	
     // 统计一定范围内的阳线数目
	m_PosKNum = PosKCount(Open, Close, Length);
	// 心理线指标计算公式
	m_Psy = m_PosKNum/Length*100;
	
	// 统计范围内，阳线数目大于初始阈值确认上行趋势
	bLong = m_Psy[1] >= PsyUpBand;
	// 统计范围内，阳线数目小于初始阈值确认下行趋势
	bShort = m_Psy[1] <= PsyDnBand;

	// 趋势上行同时没有持多仓
	If(bLong && MarketPosition != 1){
		Buy(EntryLots, Open);
		HigherAfterEntry = Open;
		LowerAfterEntry = Open;
	}
	
	// 趋势下行同时没有持空仓
	If(bShort && MarketPosition != -1){
		SellShort(EntryLots, Open);
		HigherAfterEntry = Open;
		LowerAfterEntry = Open;
	}
	
	// 持有多仓，三级止损
	If(MarketPosition == 1){
		StopLine = EntryPrice*(1-InitialStop/1000);
		If(HigherAfterEntry >= EntryPrice*(1+BreakEvenStop/1000)){
			StopLine = EntryPrice;
		}
		If(StopLine < HigherAfterEntry*(1-TrailingStop/1000)){
			StopLine = HigherAfterEntry*(1-TrailingStop/1000);
		}
		If(Low <= StopLine  && BarsSinceEntry >= 1){
			Sell(0, StopLine);
		}
	}
	// 持有空仓，三级止损
	If(MarketPosition == -1){
		StopLine = EntryPrice*(1+InitialStop/1000);
		If(LowerAfterEntry <= EntryPrice*(1-BreakEvenStop/1000)){
			StopLine = EntryPrice;
		}
		If(StopLine > LowerAfterEntry*(1+TrailingStop/1000)){
			StopLine = LowerAfterEntry*(1+TrailingStop/1000);
		}
		If(High >= StopLine && BarsSinceEntry >= 1){
			BuyToCover(0, StopLine);
		}
	}
	 
End


